package com.tomshidi.demo.utils;

import com.deepoove.poi.XWPFTemplate;
import com.deepoove.poi.config.Configure;
import com.deepoove.poi.plugin.table.LoopRowTableRenderPolicy;
import com.google.gson.Gson;
import com.google.gson.GsonBuilder;
import com.lowagie.text.DocumentException;
import com.tomshidi.demo.dto.FormDataDto;
import org.apache.commons.io.IOUtils;
import org.springframework.core.io.Resource;
import org.springframework.core.io.support.PathMatchingResourcePatternResolver;

import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

/**
 * docx文件表单动态生成
 *
 * @author tangshili
 * @since 2024/6/15 16:11
 */
public class FormDocxFileExporter {

    public static void main(String[] args) throws IOException {
        Gson gson = new GsonBuilder().create();
        String strData = "{\"title\":{\"systemName\":\"丽江市国土空间规划'一张图'实施监督系统\",\"name\":\"一键审图结果\"},\"record\":{\"userKey\":\"审查人\",\"userValue\":\"xxx\",\"checkTimeKey\":\"审查时间\",\"checkTimeValue\":\"2024-05-16 14:00\"},\"project\":{\"name\":\"xxx项目\",\"markName\":\"一键审图标绘列表\",\"landRange\":[{\"name\":\"地块1\",\"type\":\"湿地\",\"area\":\"28.89\",\"image\":\"data:image/png;base64,\"},{\"name\":\"总面积\",\"type\":\"/\",\"area\":\"28.89\",\"image\":\"base64,src:\"}]},\"layerLink\":[{\"name\":\"与土地利用总体规划图层重叠情况\",\"geoData\":[{\"name\":\"地块1\",\"type\":\"商用\",\"layerField\":[{\"fieldAlias\":\"林地\",\"area\":\"600.88\"},{\"fieldAlias\":\"湿地\",\"area\":\"600.88\"},{\"fieldAlias\":\"耕地\",\"area\":\"30\"},{\"fieldAlias\":\"小计\",\"area\":\"650.88\"}],\"fieldAlias\":[\"林地\",\"湿地\",\"耕地\",\"小计\"],\"area\":[\"600.88\",\"20\",\"30\",\"650.88\"],\"image\":\"data:image/png;base64,\"}]},{\"name\":\"与专项规划图层重叠情况\",\"geoData\":[{\"name\":\"地块1\",\"type\":\"商用\",\"fieldAlias\":[\"林地\",\"湿地\",\"耕地\",\"小计\"],\"area\":[\"600.88\",\"20\",\"30\",\"650.88\"],\"image\":\"base64,src:\"}]}]}";
        FormDataDto formData = gson.fromJson(strData, FormDataDto.class);
        Path directory = Paths.get("E:\\git clone\\tomshidi-springcloud-demo", "docx-export");
        PathMatchingResourcePatternResolver resourcePatternResolver = new PathMatchingResourcePatternResolver();
        Resource templateResource = resourcePatternResolver.getResource("classpath:templates/一键审图报告模板.docx");
        File docxFile = createDocxFile(formData, templateResource.getInputStream(), directory);
        WordToPDFConverter.word2pdf(docxFile);
    }

    public static File createDocxFile(FormDataDto formData, InputStream templateFileStream, Path outputPath) throws IOException {
        if (templateFileStream == null) {
            throw new RuntimeException("template file does not exist");
        }
        formData.buildPoiImage();
        outputPath.toFile().mkdirs();
        Path outputFilePath = outputPath.resolve( "数据填充测试.docx");
        OutputStream outputStream = Files.newOutputStream(outputFilePath);
        // 表格行循环
        LoopRowTableRenderPolicy policy = new LoopRowTableRenderPolicy();
        Configure config = Configure.builder()
                .bind("project.landRange", policy)
                .bind("geoData", policy)
                .bind("layerField", policy)
                .bind("layerLink", policy)
                .build();
        XWPFTemplate template = XWPFTemplate.compile(templateFileStream, config)
                .render(formData);
        template.writeAndClose(outputStream);
        return outputFilePath.toFile();
    }
}
